import React, { useEffect, useState, useMemo } from "react";
import { debounce } from 'lodash';

const fetchData = debounce(async (currentPage, pageSize, setStocks, setLoading) => {
  console.log(`Fetching data for page ${currentPage} with page size ${pageSize}`);
  try {
    let response = await fetch(
      `https://financialmodelingprep.com/api/v3/stock/list?apikey=OfWkW6me85Ix6iPN2IpXXpZypva6P6ja&page=${currentPage}&limit=${pageSize}`
    );
    let data = await response.json();
    console.log("Data received:", data);
    setStocks(data);
    setLoading(false);
  } catch (error) {
    console.error("Error fetching data:", error);
    setLoading(false);
  }
}, 300); // Debounce time in milliseconds

const Stocks = React.memo(() => {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10); // Set your desired page size

  useEffect(() => {
    fetchData(currentPage, pageSize, setStocks, setLoading);
  }, [currentPage, pageSize]);

  const memoizedStocks = useMemo(() => stocks, [stocks]);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
    setLoading(true); // Set loading to true when changing pages
  };

  const handlePageSizeChange = (newPageSize) => {
    setPageSize(newPageSize);
    setCurrentPage(1); // Reset to the first page when changing page size
    setLoading(true); // Set loading to true when changing page size
  };

  return (
    <>
      <h2 className="firstdiv">Get the latest Stock Price</h2>
      <div className="mainDiv">
        {/* Controls for changing page size */}
        <div className="pageSizeControls">
          <label htmlFor="pageSize">Items per page:</label>
          <select
            id="pageSize"
            value={pageSize}
            onChange={(e) => handlePageSizeChange(Number(e.target.value))}
          >
            <option value={5}>5</option>
            <option value={10}>10</option>
            <option value={20}>20</option>
          </select>
        </div>

        {loading ? (
          <p>Loading...</p>
        ) : memoizedStocks.length > 0 ? (
          memoizedStocks.map((ele, index) => (
            <div className="card" key={index}>
              <h3>{ele.symbol}</h3>
              <div className="card-body">
                <p className="card-text">{ele.name}</p>
              </div>
            </div>
          ))
        ) : (
          <p>No data available.</p>
        )}

        {/* Pagination Controls */}
        <div className="pagination">
          <button
            disabled={currentPage === 1}
            onClick={() => handlePageChange(currentPage - 1)}
          >
            Previous
          </button>
          <span> Page {currentPage} </span>
          <button onClick={() => handlePageChange(currentPage + 1)}>
            Next
          </button>
        </div>
      </div>
    </>
  );
});

export default Stocks;
