import React, { useState } from "react";

const CompanyDetails = () => {
  const [symbol, setSymbol] = useState("");
  const [companyDetails, setCompanyDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchCompanyDetails = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(
        `https://financialmodelingprep.com/api/v3/quote/${symbol}?apikey=OfWkW6me85Ix6iPN2IpXXpZypva6P6ja`
      );
      const data = await response.json();

      if (data && data.length > 0) {
        setCompanyDetails(data[0]);
      } else {
        setCompanyDetails(null);
        setError("Company not found");
      }
    } catch (error) {
      console.error("Error fetching company details:", error);
      setError("Error fetching data");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchCompanyDetails();
  };

  return (
    <div className="">
    <h2>Get the Share/Stock Price for any Company</h2>
      <h2>Provide the Company Details</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Company Symbol:
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
          />
        </label>
        <button type="submit" disabled={loading}>
          Get Details
        </button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {companyDetails && (
        <div>
          <h3>{companyDetails.symbol}</h3>
          <p>Name: {companyDetails.name}</p>
          <p>Price: {companyDetails.price}</p>
          {/* Add more details as needed */}
          <p>changesPercentage:{companyDetails.changesPercentage}</p>
          <p>change: {companyDetails.change}</p>
          <p>Percentage Change: {companyDetails.changesPercentage}%</p>
          <p>Day Low: {companyDetails.dayLow}</p>
          <p>Day High: {companyDetails.dayHigh}</p>
          <p>Year Low: {companyDetails.yearLow}</p>
          <p>Year High: {companyDetails.yearHigh}</p>
          <p>Market Cap: {companyDetails.marketCap}</p>
          <p>Price Avg 50: {companyDetails.priceAvg50}</p>
          <p>Price Avg 200: {companyDetails.priceAvg200}</p>
          <p>Exchange: {companyDetails.exchange}</p>
          <p>Volume: {companyDetails.volume}</p>
          <p>Average Volume: {companyDetails.avgVolume}</p>
          <p>Open: {companyDetails.open}</p>
          <p>Previous Close: {companyDetails.previousClose}</p>
          <p>Earnings per Share (EPS): {companyDetails.eps}</p>
          <p>Price to Earnings (P/E) Ratio: {companyDetails.pe}</p>
          <p>Earnings Announcement: {companyDetails.earningsAnnouncement}</p>
          <p>Shares Outstanding: {companyDetails.sharesOutstanding}</p>
          <p>Timestamp: {companyDetails.timestamp}</p>
        </div>
      )}
    </div>
  );
};

export default CompanyDetails;
