import React, { useState } from "react";

const CompanyDetails = () => {
  const [symbol, setSymbol] = useState("");
  const [companyDetails, setCompanyDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchCompanyDetails = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(
        `https://financialmodelingprep.com/api/v3/quote/${symbol}?apikey=OfWkW6me85Ix6iPN2IpXXpZypva6P6ja`
      );
      const data = await response.json();

      if (data && data.length > 0) {
        setCompanyDetails(data[0]);
      } else {
        setCompanyDetails(null);
        setError("Company not found");
      }
    } catch (error) {
      console.error("Error fetching company details:", error);
      setError("Error fetching data");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchCompanyDetails();
  };

  return (
    <div className="getinput">
    <h2> Instantly Discover the Share/Stock Price at Your Fingertips</h2>
      <h3>Provide the Company Details</h3>
      <form onSubmit={handleSubmit}>
        <label required>
          Enter Company Symbol:
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
          />
        </label>
        <button type="submit" disabled={loading}>
          Get Details
        </button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {companyDetails && (
        <div className="data">
          <h3>{companyDetails.symbol}</h3>
            <div className="column1">
          <p>Name: <div className="design"> {companyDetails.name}</div></p>
          <p>Price: <div className="design">{companyDetails.price}</div></p>
          <p>Change: <div className="design">{companyDetails.change}</div></p>
          <p>Percentage Change: <div className="design">{companyDetails.changesPercentage}%</div></p>
          <p>Day Low: <div className="design">{companyDetails.dayLow}</div></p>
          <p>Day High: <div className="design">{companyDetails.dayHigh}</div></p>
          <p>Year Low: <div className="design">{companyDetails.yearLow}</div></p>
          <p>Year High: <div className="design">{companyDetails.yearHigh}</div></p>
          <p>Market Cap: <div className="design">{companyDetails.marketCap}</div></p>
          <p>Price Avg 50: <div className="design">{companyDetails.priceAvg50}</div></p>
          <p>Price Avg 200: <div className="design">{companyDetails.priceAvg200}</div></p>
          <p>Exchange: <div className="design">{companyDetails.exchange}</div></p>
          <p>Volume: <div className="design">{companyDetails.volume}</div></p>
          <p>Average Volume: <div className="design">{companyDetails.avgVolume}</div></p>
          <p>Open: <div className="design">{companyDetails.open}</div></p>
          <p>Previous Close: <div className="design">{companyDetails.previousClose}</div></p>
          <p>Earnings per Share (EPS): <div className="design">{companyDetails.eps}</div></p>
          <p>Price to Earnings (P/E) Ratio: <div className="design">{companyDetails.pe}</div></p>
          <p>Earnings Announcement: <div className="design">{companyDetails.earningsAnnouncement}</div></p>
          <p>Shares Outstanding: <div className="design">{companyDetails.sharesOutstanding}</div></p>
          <p>Timestamp: <div className="design">{companyDetails.timestamp}</div></p>
            </div>
            <div className="column2"></div>
        </div>
      )}
    </div>
  );
};

export default CompanyDetails;
