import {useState} from 'react'

const Content = () => {
    //let name = "Siddhi Parekh";
    const [name, setName] = useState("SIDDHI");
    const [age, setAge] = useState(19);
    const [blogs, setBlogs] = useState([
        {title: "My new Website, body", body: 'lorem ipsum...', id:1},
        {title: "My new Website, body", body: 'lorem ipsum...', id:2},
        {title: "My new Website, body", body: 'lorem ipsum...', id:3}
    ]);
    const handleClick = () => {
        setName ("Siddhi");
        setAge(20);
    }
    const link = "https://www.google.com";

    return (  
        <div className="home">

            <button class="btn" onClick={handleClick}>Click Me</button>
            <div className="para">
                <p>{name} is {age} years old</p>
                <p>Hello!!!</p>
                <a href = {link}>Google</a>
                <br></br>
                {blogs.map((blog) => (
                    <div className='blog-preview' key={blog.id}>
                        <h2>{ blog.title }</h2>
                        <p>{ blog.body }</p>
                    </div>
                ))}
            </div>
        </div>
    );
}



export default Content;

